import net.dv8tion.jda.core.AccountType;import net.dv8tion.jda.core.JDABuilder;import net.dv8tion.jda.core.events.message.MessageReceivedEvent;import net.dv8tion.jda.core.hooks.ListenerAdapter;import sun.plugin2.message.Message;import javax.security.auth.login.LoginException;public class Main extends ListenerAdapter {    public static void main(String[] args) throws LoginException {        JDABuilder builder = new JDABuilder(AccountType.BOT);        String token = "NTA0MjUyMjA1MDk3MDI1NTQ2.Drj5Xw.AieFJnynuPU5TixT4ET6Y6c122o";        builder.setToken(token);        builder.addEventListener(new Main());        builder.buildAsync();    }    @Override    public void onMessageReceived(MessageReceivedEvent event) {        System.out.println("We recevied a message from: " + event.getAuthor().getName() + event.getMessage().getContentDisplay());        if(event.getMessage().getContentRaw().equals("&ping")) {            event.getChannel().sendMessage("Pong").queue();        }        else if (event.getMessage().getContentRaw().contains("&")) {            event.getChannel().sendMessage("Command not recornised!").queue();        }    }}